String.prototype.formatTime = function(cull) {
  var sec_num = parseInt(this, 10); // don't forget the second param
  var hours   = Math.floor(sec_num / 3600);
  var minutes = Math.floor((sec_num - (hours * 3600)) / 60);
  var seconds = sec_num - (hours * 3600) - (minutes * 60);

  if (hours < 10) {
    hours = "0"+hours;
    if (cull) {
      if (cull >= 2) {
        hours = hours;
        if (!hours) {
          hours = "";
        }
        else {
          hours = hours + ":";
        }
      }
      else {
        hours = "";
      }
    }
    else {
      hours = hours + ":";
    }
  }
  if (minutes < 10) {
    minutes = "0"+minutes;
    if (cull) {
      if (cull >= 1) {
        minutes = minutes;
        if (!minutes) {
          minutes = "";
        }
        else {
          minutes = minutes + ":";
        }
      }
      else {
        minutes = "";
      }
    }
    else {
      minutes = minutes + ":";
    }
  }
  else {
    minutes = minutes + ":";
  }
  if (seconds < 10) {seconds = "0"+seconds;}
  return hours+minutes+seconds;
}

String.prototype.valid = function() {
  if (this.length <= 0 || this.trim().length <= 0) {
    return;
  }
  return this.trim();
}

String.prototype.remove = function(valueToRemove) {
  if (!this.valid()) {
    return this.valid();
  }
  var arr = JSON.parse("[" + this + "]");

  for (var index in arr) {
    if (arr[index] == valueToRemove) {
      arr.splice(index, 1);
    }
  }
  return arr.toString();
}

String.prototype.push = function(value) {
  var arr;
  if (!this.valid()) {
    arr = [];
  }
  else {
    arr = JSON.parse("[" + this + "]");
  }
  arr.push(value);

  return arr.toString();
}

