sync.traverse = function(object, string, value) {
  var split = String(string || "").split(".");
  var target = object;
  while (string && split.length) {
    var key = split[0];
    split.splice(0, 1);
    if (target[key] && (value == null || split.length)) {
      target = target[key];
    }
    else if (isNaN(key) && !target[key] && (value == null || split.length)) { // reading only
      // case insensitive reading
      var searching = false;
      for (var refKey in target) {
        if (isNaN(refKey) && refKey.toLowerCase() == key.toLowerCase()) {
          target = target[refKey];
          searching = true;
          break;
        }
      }
      if (!searching) {
        if (value != null && split.length) {
          if (!target[key]) {
            target[key] = {};
          }
          target = target[key];
        }
        else if (value != null) {
          if (value === "") {
            if (Array.isArray(target)) {
              target.splice(key, 1);
            }
            else {
              delete target[key];
            }
            return;
          }
          else {
            target[key] = value;
            target = target[key];
          }
        }
        else {
          return false;
        }
      }
    }
    else {
      if (value != null && split.length) {
        if (!target[key]) {
          target[key] = {};
        }
        target = target[key];
      }
      else if (value != null) {
        if (value === "") {
          if (Array.isArray(target)) {
            target.splice(key, 1);
          }
          else {
            delete target[key];
          }
          return;
        }
        else {
          target[key] = value;
          target = target[key];
        }
      }
      else {
        return false;
      }
    }
  }
  return target;
}

