function buildShape(shape, col, size) {
  size = size || 20;

  var dummyCanvas = $("<canvas>");
  dummyCanvas.attr("width", (size || 20) + "px");
  dummyCanvas.attr("height", (size || 20) + "px");
  dummyCanvas.css("pointer-events", "none");
  if (shape == 0) {
    dummyCanvas.drawRect({
      width : size, height : size,
      fillStyle : col,
      strokeStyle: "rgba(0,0,0,0.4)",
      strokeWidth: 2,
      fromCenter : false,
    });
  }
  else if (shape == 1) {
    dummyCanvas.drawRect({
      width : size, height : size,
      fillStyle : col,
      strokeStyle: "rgba(0,0,0,0.4)",
      strokeWidth: 2,
      fromCenter : false,
      cornerRadius : 4
    });
  }
  else if (shape == 2) {
    dummyCanvas.drawRect({
      width : size, height : size,
      fillStyle : col,
      strokeStyle: "rgba(0,0,0,0.4)",
      strokeWidth: 2,
      fromCenter : false,
      cornerRadius : 30
    });
  }
  else if (shape == 3) {
    dummyCanvas.drawPolygon({
      radius : size/2,
      y : -2,
      fillStyle : col,
      strokeStyle: "rgba(0,0,0,0.4)",
      strokeWidth: 2,
      fromCenter : false,
      rotate : 180,
      sides : 3,
    });
  }
  else if (shape == 4) {
    dummyCanvas.drawPolygon({
      radius : size/2,
      fillStyle : col,
      strokeStyle: "rgba(0,0,0,0.4)",
      strokeWidth: 2,
      fromCenter : false,
      disableEvents : true,
      sides : 3
    });
  }
  else if (shape == 5) {
    dummyCanvas.drawPolygon({
      radius : size/2,
      fillStyle : col,
      strokeStyle: "rgba(0,0,0,0.4)",
      strokeWidth: 2,
      fromCenter : false,
      disableEvents : true,
      concavity : 0.5,
      sides : 5
    });
  }
  else if (shape) {
    dummyCanvas.drawPolygon({
      radius : size/2,
      fillStyle : col,
      strokeStyle: "rgba(0,0,0,0.4)",
      strokeWidth: 2,
      fromCenter : false,
      disableEvents : true,
      sides : shape-1
    });
  }
  return dummyCanvas;
}
