                  var cX = cursorData.mX;
                  var cY = cursorData.mY;

                  cursorLayer.lineStyle(2, col);
                  cursorLayer.moveTo(cX, cY);
                  cursorLayer.lineTo(finalX, finalY);

                  var a = finalX - cX;
                  var b = finalY - cY;
                  var dist = Math.sqrt(a*a + b*b);

